# PowerShell script to create missing EXMODZ files
# Run this from any location - it will place files in the correct folders

$repo = "C:\Users\finla\Documents\GitHub\Icarus-mods"

# Agents_BioLab.EXMODZ
$biolab = ""

# Indestructible_Tools_Weapons.EXMODZ
$tools = ""

# No_Food_Spoilage.EXMODZ
$food = " pIFJAAAARXh0cmFjdGVkIE1vZHMvTm9fRm9vZF9TcG9pbGFnZS5FWE1PRFVUBQADWBpcaXV4CwABBAAAAAAEAAAAAFBLBQYAAAAAAgACAMAAAADaAgAAAAA="

Write-Host "Creating EXMODZ files..." -ForegroundColor Cyan

# Create Agents_BioLab.EXMODZ
$bytes = [Convert]::FromBase64String($biolab)
[IO.File]::WriteAllBytes("$repo\Agents_BioLab\Agents_BioLab.EXMODZ", $bytes)
Write-Host "  [OK] Agents_BioLab.EXMODZ" -ForegroundColor Green

# Create Indestructible_Tools_Weapons.EXMODZ
$bytes = [Convert]::FromBase64String($tools)
[IO.File]::WriteAllBytes("$repo\Indestructible_Tools_Weapons\Indestructible_Tools_Weapons.EXMODZ", $bytes)
Write-Host "  [OK] Indestructible_Tools_Weapons.EXMODZ" -ForegroundColor Green

# Create No_Food_Spoilage.EXMODZ
$bytes = [Convert]::FromBase64String($food)
[IO.File]::WriteAllBytes("$repo\No_Food_Spoilage\No_Food_Spoilage.EXMODZ", $bytes)
Write-Host "  [OK] No_Food_Spoilage.EXMODZ" -ForegroundColor Green

# Cleanup - remove old files and scripts
Write-Host "`nCleaning up..." -ForegroundColor Cyan
Remove-Item "$repo\setup_repo.bat" -ErrorAction SilentlyContinue
Remove-Item "$repo\cleanup.bat" -ErrorAction SilentlyContinue
Write-Host "  [OK] Removed old scripts" -ForegroundColor Green

Write-Host "`n========================================" -ForegroundColor Yellow
Write-Host "  All done! Now just commit and push:" -ForegroundColor Yellow  
Write-Host "========================================" -ForegroundColor Yellow
Write-Host ""
Write-Host "  cd $repo"
Write-Host "  git add ."
Write-Host '  git commit -m "Added all EXMODZ mod files"'
Write-Host "  git push"
Write-Host ""
